mapper.select:
SELECT rn AS rowNumber, qna_number, qna_title, qna_contents, qna_writer, qna_date, qna_ref, qna_step, qna_ref_order, qna_answer_number, qna_parent_number, qna_type, qna_status, qna_secret, member_id
FROM ( SELECT ROW_NUMBER() OVER (ORDER BY qna_ref DESC) AS rn, qna_number, qna_title, qna_contents, qna_writer, qna_date, qna_ref, qna_step, qna_ref_order, qna_answer_number, qna_parent_number, qna_type, qna_status, qna_secret, member_id
FROM qna
WHERE qna_step = 0
) AS derived
WHERE rn BETWEEN #{startNo} AND #{endNo}
AND qna_step = 0;

mapper.update:
UPDATE qna
SET qna_writer = #{qna_writer}, qna_title = #{qna_title}, qna_contents = #{qna_contents}, qna_secret = #{qna_secret}
WHERE qna_number = #{qna_number}
